import React, {useState} from "react";
import {SafeAreaView, ScrollView, StatusBar, StyleSheet, View} from "react-native";
import {Avatar, Button, Caption, TextInput} from 'react-native-paper';
import {useNavigation} from "@react-navigation/native";
import {useStoreActions} from "easy-peasy";
import DropDownPicker from 'react-native-dropdown-picker'

const AddScreen = () => {
    const navigation = useNavigation();
    const add = useStoreActions((actions) => actions.addExpense);

    const [description, setDescription] = useState("");
    const [amount, setAmount] = useState("");
    const [selectedCategory, setSelectedCategory] = useState();
    const [selectedVariant, setSelectedVariant] = useState();
    const [selectedPrice, setSelectedPrice] = useState();

    const addExpense = () => {
        if (description !== "" && amount !== "") {
            add({description, amount});
            navigation.navigate("Home");
        }
    };

    return (
        <ScrollView
            showsVerticalScrollIndicator={false}
            style={styles.container}>
            <SafeAreaView>
                <View style={{height: 4}}/>
                <StatusBar
                    animated={true}
                    backgroundColor="#61dafb"/>
                <View style={{flex: 1, marginTop: 10, alignItems: 'center'}}>
                    <Avatar.Image size={100} source={require('../../assets/favicon.png')}/>
                    <Caption>Product Logo</Caption>
                </View>
                <View style={{height: 10}}/>
                <TextInput
                    style={{
                        backgroundColor: "#fff"
                    }}
                    mode="outlined"
                    label="Product Name"
                    placeholder="Type your product name"
                />
                <View style={{height: 10}}/>
                <TextInput
                    style={{
                        backgroundColor: "#fff"
                    }}
                    mode="outlined"
                    label="Product Description"
                    placeholder="Type your product description"
                />
                <View style={{height: 10}}/>
                <TextInput
                    style={{
                        backgroundColor: "#fff"
                    }}
                    mode="outlined"
                    label="Product Website"
                    placeholder="Type your product website"
                />
                <View style={{height: 10}}/>
                <DropDownPicker
                    items={[
                        {label: 'English', value: 'en'},
                        {label: 'Deutsch', value: 'de'},
                        {label: 'French', value: 'fr'},
                    ]}
                    defaultIndex={1}
                    containerStyle={{height: 40}}
                    onChangeItem={item => console.log(item.label, item.value)}
                />
                {/*<View style={{height: 20}}/>*/}
                {/*<Button icon="rocket" mode="contained" onPress={() => console.log('Pressed')}>*/}
                {/*    SUBMIT PRODUCT FOR REVIEW*/}
                {/*</Button>*/}
                {/*<View style={{height: 30}}/>*/}
            </SafeAreaView>
        </ScrollView>
    );
};
const styles = StyleSheet.create({
    container: {
        flex: 1,
        paddingLeft: 15,
        paddingRight: 15,
        marginTop: 20,
        paddingBottom: 15,
        backgroundColor: "#fff",
    },
});

export default AddScreen;
