import React, {useState} from "react";
import {Linking, SafeAreaView, ScrollView, StatusBar, StyleSheet, View} from "react-native";
import {Avatar, Button, Card, Chip, Paragraph, Snackbar, Subheading, Title, TouchableRipple} from 'react-native-paper';
import {useNavigation} from "@react-navigation/native";
import {useStoreActions} from "easy-peasy";
import {Ionicons} from "@expo/vector-icons";

const HomeDetailScreen = () => {
    const navigation = useNavigation();
    const add = useStoreActions((actions) => actions.addExpense);

    const [description, setDescription] = useState("");
    const [amount, setAmount] = useState("");
    const [selectedCategory, setSelectedCategory] = useState();
    const [selectedVariant, setSelectedVariant] = useState();
    const [selectedPrice, setSelectedPrice] = useState();
    const [visible, setVisible] = React.useState(false);
    const onToggleSnackBar = () => setVisible(!visible);
    const onDismissSnackBar = () => setVisible(false);

    const addExpense = () => {
        if (description !== "" && amount !== "") {
            add({description, amount});
            navigation.navigate("Home");
        }
    };

    return (
        <ScrollView
            showsVerticalScrollIndicator={false}
            style={styles.container}>
            <SafeAreaView>
                <View style={{height: 4}}/>
                <StatusBar
                    animated={true}
                    backgroundColor="#61dafb"/>
                <View style={{flex: 1, marginTop: 10, alignItems: 'center'}}>
                    <Avatar.Image size={100} source={require('../../assets/favicon.png')}/>
                </View>
                <View style={{height: 10}}/>
                <Card style={{padding: 10}}>
                    <Card.Content>
                        <Title>Product Name</Title>
                        <Paragraph>Product Description </Paragraph>
                    </Card.Content>
                    <View style={{height: 10}}/>
                    <View style={styles.row}>
                        <Chip icon="cash" onPress={() => {
                        }} style={styles.chip}>
                            Free Options
                        </Chip>
                        <Chip icon="apps" onPress={() => {
                        }} style={styles.chip}>
                            Android
                        </Chip>
                    </View>
                    <View style={{height: 10}}/>
                    <View style={styles.row}>
                        <Chip icon="shape" onPress={() => {
                        }} style={{...styles.chip, flex: 1}}>
                            Social Media
                        </Chip>
                    </View>
                    <View style={{height: 20}}/>
                    <TouchableRipple
                        onPress={onToggleSnackBar}>{visible ? 'Hide' : 'Show'}>
                        <View style={{
                            flex: 1,
                            borderWidth: 1,
                            borderColor: 'lightgray',
                            borderRadius: 10,
                            padding: 5,
                            marginTop: 5,
                            justifyContent: 'center',
                            flexDirection: 'column',
                            alignItems: 'center'
                        }}>
                            <Ionicons name="caret-up" size={25}/>
                            <Subheading>1</Subheading>
                        </View>
                    </TouchableRipple>
                    <View style={{height: 25}}/>
                    <Button icon="account" mode="text"
                            onPress={() => Linking.openURL('mailto:rajakaleemlab@gmail.com?subject=ProductExplore | Feedback&body=Hello!')}>
                        CONTACT OWNER
                    </Button>
                    <View style={{height: 20}}/>
                    <Button icon="link" mode="contained"
                            onPress={() => Linking.openURL('mailto:rajakaleemlab@gmail.com?subject=ProductExplore | Feedback&body=Hello!')}>
                        VISIT PRODUCT WEBSITE
                    </Button>
                    <View style={{height: 20}}/>
                </Card>
                <View style={{height: 30}}/>
                <Snackbar
                    visible={visible}
                    onDismiss={onDismissSnackBar}
                    action={{
                        label: 'Undo',
                        onPress: () => {
                            // Do something
                        },
                    }}>
                    Hey there! I'm a Snackbar.
                </Snackbar>
            </SafeAreaView>
        </ScrollView>
    );
};
const styles = StyleSheet.create({
    container: {
        flex: 1,
        paddingLeft: 5,
        paddingRight: 5,
        marginTop: 20,
        paddingBottom: 15,
        backgroundColor: "#fff",
    },
    row: {
        flexDirection: 'row',
        flexWrap: 'wrap',
        flex: 1,
    },
    chip: {
        margin: 4,
        flex: 0.50,
        backgroundColor: 'lightgray'
    },
});

export default HomeDetailScreen;
