import React, {useState} from "react";
import {useNavigation} from "@react-navigation/native";
import {Button, Card, Headline, TextInput} from "react-native-paper";
import {Alert, SafeAreaView, ScrollView, StyleSheet, View} from "react-native";
import Firebase from '../../firebase';

const auth = Firebase.auth();

const SignUpScreen = () => {
    const navigation = useNavigation();
    const [email, setEmail] = useState('');
    const [name, setName] = useState('');
    const [password, setPassword] = useState('');
    const [phoneNumber, setPhoneNumber] = useState('');

    const onHandleSignup = async () => {
        try {
            if (email !== '' && password !== '') {
                // navigation.navigate('SignIn')
                await auth.createUserWithEmailAndPassword(email, password).then(
                    () => Alert.alert(
                        "Signed Up Successfully!",
                        "Please verify your email, and then Sign In!",
                        [
                            {text: 'Ok', onPress: () => console.log('Ok Pressed')},
                        ],
                        {cancelable: false}));
            }
        } catch (error) {
            alert(error.message);
        }
    };

    return (
        <ScrollView
            showsVerticalScrollIndicator={false}
            style={styles.container}>
            <SafeAreaView/>
            <Card.Cover source={require("../../assets/signup-img-online.jpeg")}/>
            <View style={{height: 5}}></View>
            <Headline style={{fontWeight: 'bold'}}>Sign Up</Headline>
            <View style={{height: 10}}></View>
            <TextInput
                style={{
                    backgroundColor: "#fff"
                }}
                mode="outlined"
                label="Name"
                autoCapitalize='none'
                textContentType='name'
                value={name}
                onChangeText={text => setName(text)}
                placeholder="Type your name"
            />
            <View style={{height: 10}}/>
            <TextInput
                style={{
                    backgroundColor: "#fff"
                }}
                mode="outlined"
                label="Email"
                autoCapitalize='none'
                keyboardType='email-address'
                textContentType='emailAddress'
                autoFocus={true}
                value={email}
                onChangeText={text => setEmail(text)}
                placeholder="Type your email"
            />
            <View style={{height: 10}}/>
            <TextInput
                style={{
                    backgroundColor: "#fff"
                }}
                mode="outlined"
                label="Phone Number"
                autoCapitalize='none'
                textContentType='telephoneNumber'
                value={phoneNumber}
                onChangeText={text => setPhoneNumber(text)}
                placeholder="Type your phone number"
            />
            <View style={{height: 10}}/>
            <TextInput
                style={{
                    backgroundColor: "#fff"
                }}
                mode="outlined"
                label="Password"
                placeholder="Type your password"
                autoCapitalize='none'
                autoCorrect={false}
                secureTextEntry={true}
                textContentType='password'
                value={password}
                onChangeText={text => setPassword(text)}
            />
            <View style={{height: 15}}/>
            <Button mode="contained" onPress={onHandleSignup}>
                SIGN UP
            </Button>
            <View style={{height: 10}}/>
            <Button mode="text" onPress={() => navigation.navigate('SignIn')}>
                Already Signed Up? Sign In here
            </Button>
            <View style={{height: 30}}/>
            <View style={{height: 20}}/>

        </ScrollView>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        paddingLeft: 15,
        paddingRight: 15,
        paddingTop: 35,
        paddingBottom: 15,
    },
});
export default SignUpScreen;
