import React, {useState} from "react";
import {Button, SafeAreaView, ScrollView, StatusBar, StyleSheet, TextInput, View} from "react-native";
import {useStoreActions} from "easy-peasy";
import {useNavigation} from "@react-navigation/native";

import {MainContainer} from "../components";
import {Avatar} from "react-native-paper";

const AddScreen = () => {
    const navigation = useNavigation();
    const add = useStoreActions((actions) => actions.addExpense);

    const [description, setDescription] = useState("");
    const [amount, setAmount] = useState("");

    const addExpense = () => {
        if (description !== "" && amount !== "") {
            add({description, amount});
            navigation.navigate("Home");
        }
    };

    return (
        <ScrollView
            showsVerticalScrollIndicator={false}
            style={styles.container}>
            <SafeAreaView>
                <View style={{height: 4}}/>
                <StatusBar
                    animated={true}
                    backgroundColor="#61dafb"/>
                <View style={{flex: 1, marginTop: 10, alignItems: 'center'}}>
                    <Avatar.Image size={100} source={require('../../assets/favicon.png')}/>
                </View>
                <View style={{height: 10}}/>
                <TextInput
                    style={{
                        backgroundColor: "#fff"
                    }}
                    mode="outlined"
                    label="Name"
                    placeholder="Type your name"
                />
                <View style={{height: 10}}/>
                <TextInput
                    style={{
                        backgroundColor: "#fff"
                    }}
                    mode="outlined"
                    label="Email"
                    placeholder="Type your email"
                />
                <View style={{height: 10}}/>
                <TextInput
                    style={{
                        backgroundColor: "#fff"
                    }}
                    mode="outlined"
                    label="Phone Number"
                    placeholder="Type your phone number"
                />
                <View style={{height: 10}}/>

                <TextInput
                    style={{
                        backgroundColor: "#fff"
                    }}
                    mode="outlined"
                    label="Password"
                    placeholder="Type your password"
                />
                <View style={{height: 10}}/>
                <TextInput
                    style={{
                        backgroundColor: "#fff"
                    }}
                    mode="outlined"
                    label="Bio"
                    placeholder="Type your bio"
                />
                <View style={{height: 10}}/>
                <TextInput
                    style={{
                        backgroundColor: "#fff"
                    }}
                    mode="outlined"
                    label="Website"
                    placeholder="Type your website link"
                />
                <View style={{height: 20}}/>

                <View style={{height: 30}}/>
            </SafeAreaView>
        </ScrollView>
    );
};
const styles = StyleSheet.create({
    input: {
        height: 40,
        padding: 10,
        borderColor: "#ccc",
        borderWidth: 1,
        marginBottom: 20,
    },
});

export default AddScreen;
