import React, {useState} from "react";
import {SafeAreaView, ScrollView, StatusBar, StyleSheet, View} from "react-native";
import {Avatar, Button, Caption, Chip, List, RadioButton, TextInput} from 'react-native-paper';
import {useNavigation} from "@react-navigation/native";
import {useStoreActions} from "easy-peasy";
import {Picker} from "@react-native-picker/picker";
import {submitPost} from "../api/firebaseAPI";

const AddScreen = () => {
    const navigation = useNavigation();
    const add = useStoreActions((actions) => actions.addExpense);

    let [name, setName] = useState("");
    let [description, setDescription] = useState("");
    let [website, setWebsite] = useState("");
    const [selectedCategory, setSelectedCategory] = useState();
    const [selectedVariant, setSelectedVariant] = useState();
    let [selectedPrice, setSelectedPrice] = useState();

    const addPost = () => {

        submitPost()

        description = "123";
        selectedPrice = "123";
        add({description, amount});
    };

    return (
        <ScrollView
            showsVerticalScrollIndicator={false}
            style={styles.container}>
            <SafeAreaView>
                <View style={{height: 4}}/>
                <StatusBar
                    animated={true}
                    backgroundColor="#61dafb"/>
                <View style={{flex: 1, marginTop: 10, alignItems: 'center'}}>
                    <Avatar.Image size={100} source={require('../../assets/favicon.png')}/>
                    <Caption style={{marginTop: 5}}>Product Logo</Caption>
                </View>
                <View style={{height: 10}}/>
                <TextInput
                    style={{
                        backgroundColor: "#fff"
                    }}
                    mode="outlined"
                    label="Product Name"
                    placeholder="Type your product name"
                />
                <View style={{height: 10}}/>
                <TextInput
                    style={{
                        backgroundColor: "#fff"
                    }}
                    mode="outlined"
                    label="Product Description"
                    placeholder="Type your product description"
                />
                <View style={{height: 10}}/>
                <TextInput
                    style={{
                        backgroundColor: "#fff"
                    }}
                    mode="outlined"
                    label="Product Website"
                    placeholder="Type your product website"
                />
                <List.Section title="Product Pricing Model">
                    <View style={styles.row}>
                        <Chip icon="information" onPress={() => {
                        }} style={styles.chip}>
                            Free
                        </Chip>
                        <Chip icon="information" onPress={() => {
                        }} style={styles.chip}>
                            Mix
                        </Chip>
                        <Chip icon="information" onPress={() => {
                        }} style={styles.chip}>
                            Paid
                        </Chip>
                    </View>
                </List.Section>
                <List.Section title="Product Operating System">
                    <RadioButton.Group
                        style={{
                            flexDirection: 'column',
                            flexWrap: 'wrap',
                            flex: 1,
                        }}
                        onValueChange={newValue => setSelectedVariant(newValue)} value={selectedVariant}>
                        <RadioButton.Item style={{
                            flexWrap: 'wrap',
                            flex: 0.25,
                            margin: 3,
                            backgroundColor: 'lightgray'
                        }} label="Android" value="android"/>
                        <RadioButton.Item
                            style={{
                                flexWrap: 'wrap',
                                flex: 0.25,
                                margin: 3,
                                backgroundColor: 'lightgray'
                            }}
                            label="iOS" value="ios"/>
                        <RadioButton.Item
                            style={{
                                flexWrap: 'wrap',
                                flex: 0.25,
                                margin: 3,
                                backgroundColor: 'lightgray'
                            }} label="Website" value="website"/>
                        <RadioButton.Item
                            style={{
                                flexWrap: 'wrap',
                                flex: 0.25,
                                margin: 3,
                                backgroundColor: 'lightgray'
                            }}
                            label="Other" value="other"/>
                    </RadioButton.Group>
                </List.Section>
                <List.Section title="Product Category">
                    <Picker
                        selectedValue={selectedCategory}
                        onValueChange={(itemValue, itemIndex) =>
                            setSelectedCategory(itemValue)
                        }>
                        <Picker.Item label="Productivity" value="java"/>
                        <Picker.Item label="Tech" value="js"/>
                        <Picker.Item label="Crypto" value="js"/>
                        <Picker.Item label="Metaverse" value="js"/>
                        <Picker.Item label="Tools" value="java"/>
                        <Picker.Item label="Games" value="java"/>
                        <Picker.Item label="Freelance" value="java"/>
                        <Picker.Item label="Investing" value="java"/>
                        <Picker.Item label="Art" value="java"/>
                        <Picker.Item label="Fashion" value="java"/>
                        <Picker.Item label="Entertainment / Memes" value="java"/>
                        <Picker.Item label="Sports" value="java"/>
                        <Picker.Item label="Artificial Intelligence" value="js"/>
                        <Picker.Item label="Marketing" value="java"/>
                        <Picker.Item label="User Experience" value="js"/>
                        <Picker.Item label="Internet of Things" value="java"/>
                        <Picker.Item label="Photography" value="js"/>
                        <Picker.Item label="Growth Hacking" value="java"/>
                        <Picker.Item label="Books / Writing" value="js"/>
                        <Picker.Item label="Android App" value="java"/>
                        <Picker.Item label="API" value="js"/>
                        <Picker.Item label="Web App" value="java"/>
                        <Picker.Item label="Health and Fitness" value="js"/>
                        <Picker.Item label="iOS App" value="java"/>
                        <Picker.Item label="Weather" value="js"/>
                        <Picker.Item label="FinTech" value="js"/>
                        <Picker.Item label="Productivity" value="java"/>
                        <Picker.Item label="Science" value="js"/>
                        <Picker.Item label="Charity" value="js"/>
                        <Picker.Item label="Politics" value="js"/>
                        <Picker.Item label="Cooking" value="java"/>
                        <Picker.Item label="Music" value="js"/>
                        <Picker.Item label="Other" value="js"/>
                    </Picker>
                </List.Section>
                <View style={{height: 20}}/>
                <Button icon="rocket" mode="contained" onPress={addPost}>
                    SUBMIT PRODUCT FOR REVIEW
                </Button>
                <View style={{height: 30}}/>
            </SafeAreaView>
        </ScrollView>
    );
};
const styles = StyleSheet.create({
    container: {
        flex: 1,
        paddingLeft: 15,
        paddingRight: 15,
        marginTop: 20,
        paddingBottom: 15,
        backgroundColor: "#fff",
    },
    row: {
        flexDirection: 'row',
        flexWrap: 'wrap',
        flex: 1,
    },
    chip: {
        margin: 4,
        flex: 0.33,
        backgroundColor: 'lightgray'
    },
});

export default AddScreen;
