import React from "react";
import {FlatList, View} from "react-native";
import {Avatar, Caption, Card, Subheading} from 'react-native-paper';
import {Ionicons} from "@expo/vector-icons";

const ExpenseList = ({expenses}) => {
        return (
            <FlatList
                style={{flex: 1, width: '100%', paddingHorizontal: 10}}
                data={expenses}
                showsVerticalScrollIndicator={false}
                renderItem={({item}) => (
                    <Card
                        mode={"elevated"}
                        style={{marginVertical: 5}}>
                        <Card.Title
                            title="Card Title"
                            subtitle="Card Subtitle"
                            left={(props) =>
                                <Avatar.Image {...props}
                                              size={45}
                                              source={{uri: 'https://ph-files.imgix.net/206c70ae-d2b6-4496-b1da-76746f2ddefb.png?auto=format&auto=compress&codec=mozjpeg&cs=strip&w=80&h=80&fit=crop&bg=0fff&dpr=2'}}/>
                            }
                            rightStyle={{marginRight: 10}}
                            right={(props) =>
                                <View style={{
                                    flex: 1,
                                    borderWidth: 1,
                                    borderColor: 'lightgray',
                                    borderRadius: 10,
                                    padding: 5,
                                    marginTop: 10,
                                    justifyContent: 'center',
                                    flexDirection: 'column',
                                    alignItems: 'center'
                                }}>
                                    <Ionicons name="caret-up" size={25}/>
                                    <Subheading>1</Subheading>
                                </View>
                            }
                        />
                        <View
                            style={{
                                flexDirection: 'row',
                                justifyContent: 'center',
                                alignContent: 'center',
                                flex: 1,
                                alignItems: 'center'
                            }}>
                            <Caption style={{flex: 0.33}}>By coderraja</Caption>
                            <Caption style={{flex: 0.33}}>Free Options</Caption>
                            <Caption style={{flex: 0.33}}>Social Media Tool</Caption>
                        </View>
                    </Card>

                )}
                keyExtractor={(item) => item.id}
            />
        );
    }
;

export default ExpenseList;
