import React, {useState} from "react";
import {Linking, SafeAreaView, ScrollView, StatusBar, StyleSheet, View} from "react-native";
import {Avatar, Button, Card, Chip, List, Paragraph, RadioButton, TextInput, Title} from 'react-native-paper';
import {useNavigation} from "@react-navigation/native";
import {useStoreActions} from "easy-peasy";
import {Picker} from "@react-native-picker/picker";

const HomeDetailScreen = () => {
    const navigation = useNavigation();
    const add = useStoreActions((actions) => actions.addExpense);

    const [description, setDescription] = useState("");
    const [amount, setAmount] = useState("");
    const [selectedCategory, setSelectedCategory] = useState();
    const [selectedVariant, setSelectedVariant] = useState();
    const [selectedPrice, setSelectedPrice] = useState();

    const addExpense = () => {
        if (description !== "" && amount !== "") {
            add({description, amount});
            navigation.navigate("Home");
        }
    };

    return (
        <ScrollView
            showsVerticalScrollIndicator={false}
            style={styles.container}>
            <SafeAreaView>
                <View style={{height: 4}}/>
                <StatusBar
                    animated={true}
                    backgroundColor="#61dafb"/>
                <View style={{flex: 1, marginTop: 10, alignItems: 'center'}}>
                    <Avatar.Image size={100} source={require('../../assets/favicon.png')}/>
                </View>

                <View style={{height: 10}}/>
                <Card>
                    <Card.Content>
                        <Title>Product Name</Title>
                        <View style={{height: 10}}/>
                        <Paragraph>Product Description</Paragraph>
                    </Card.Content>
                    <View style={{height: 10}}/>
                        <View style={styles.row}>
                            <Chip icon="information" onPress={() => {
                            }} style={styles.chip}>
                                Free Options
                            </Chip>
                            <Chip icon="information" onPress={() => {
                            }} style={styles.chip}>
                                Social Media
                            </Chip>
                            <Chip icon="information" onPress={() => {
                            }} style={styles.chip}>
                                Android
                            </Chip>

                        </View>
                    <List.Section title="Product Category">
                        <Picker
                            selectedValue={selectedCategory}
                            onValueChange={(itemValue, itemIndex) =>
                                setSelectedCategory(itemValue)
                            }>
                            <Picker.Item label="Productivity" value="java"/>
                            <Picker.Item label="Tech" value="js"/>
                            <Picker.Item label="Crypto" value="js"/>
                            <Picker.Item label="Metaverse" value="js"/>
                            <Picker.Item label="Tools" value="java"/>
                            <Picker.Item label="Games" value="java"/>
                            <Picker.Item label="Freelance" value="java"/>
                            <Picker.Item label="Investing" value="java"/>
                            <Picker.Item label="Art" value="java"/>
                            <Picker.Item label="Fashion" value="java"/>
                            <Picker.Item label="Entertainment / Memes" value="java"/>
                            <Picker.Item label="Sports" value="java"/>
                            <Picker.Item label="Artificial Intelligence" value="js"/>
                            <Picker.Item label="Marketing" value="java"/>
                            <Picker.Item label="User Experience" value="js"/>
                            <Picker.Item label="Internet of Things" value="java"/>
                            <Picker.Item label="Photography" value="js"/>
                            <Picker.Item label="Growth Hacking" value="java"/>
                            <Picker.Item label="Books / Writing" value="js"/>
                            <Picker.Item label="Android App" value="java"/>
                            <Picker.Item label="API" value="js"/>
                            <Picker.Item label="Web App" value="java"/>
                            <Picker.Item label="Health and Fitness" value="js"/>
                            <Picker.Item label="iOS App" value="java"/>
                            <Picker.Item label="Weather" value="js"/>
                            <Picker.Item label="FinTech" value="js"/>
                            <Picker.Item label="Productivity" value="java"/>
                            <Picker.Item label="Science" value="js"/>
                            <Picker.Item label="Charity" value="js"/>
                            <Picker.Item label="Politics" value="js"/>
                            <Picker.Item label="Cooking" value="java"/>
                            <Picker.Item label="Music" value="js"/>
                            <Picker.Item label="Other" value="js"/>
                        </Picker>
                    </List.Section>
                </Card>
                {/*<View style={{flex: 1, marginTop: 17, alignItems: 'center'}}>*/}
                {/*    <List.Item*/}
                {/*        titleStyle={{*/}
                {/*            textAlign: 'center'*/}
                {/*        }}*/}
                {/*        style={{*/}
                {/*            backgroundColor: 'lightgray',*/}
                {/*            width: '100%',*/}
                {/*            borderRadius: 20,*/}
                {/*            alignItems: 'center',*/}
                {/*            flex: 1,*/}
                {/*            alignContent: 'center',*/}
                {/*            textAlign: 'center'*/}
                {/*        }}*/}
                {/*        onPress={() => }*/}
                {/*        title="Open Product Website"*/}
                {/*        left={props => <List.Icon {...props} icon="link"/>}*/}
                {/*    />*/}
                {/*</View>*/}
                <View style={{height: 20}}/>
                <Button icon="link" mode="contained"
                        onPress={() => Linking.openURL('mailto:rajakaleemlab@gmail.com?subject=ProductExplore | Feedback&body=Hello!')}>
                    VISIT PRODUCT WEBSITE
                </Button>
                <View style={{height: 30}}/>
            </SafeAreaView>
        </ScrollView>
    );
};
const styles = StyleSheet.create({
    container: {
        flex: 1,
        paddingLeft: 15,
        paddingRight: 15,
        marginTop: 20,
        paddingBottom: 15,
        backgroundColor: "#fff",
    },
    row: {
        flexDirection: 'row',
        flexWrap: 'wrap',
        flex: 1,
    },
    chip: {
        margin: 4,
        flex: 0.33,
        backgroundColor: 'lightgray'
    },
});

export default HomeDetailScreen;
