import React, {useState} from "react";
import {useNavigation} from "@react-navigation/native";
import {Button, Card, Headline, TextInput} from "react-native-paper";
import {SafeAreaView, ScrollView, StyleSheet, View} from "react-native";
import Firebase from '../../firebase';

const auth = Firebase.auth();

const SignUpScreen = () => {
    const navigation = useNavigation();
    const [email, setEmail] = useState('');
    const [name, setName] = useState('');
    const [password, setPassword] = useState('');
    const [phoneNumber, setPhoneNumber] = useState('');

    return (
        <ScrollView
            showsVerticalScrollIndicator={false}
            style={styles.container}>
            <SafeAreaView/>
            <Card.Cover source={require("../../assets/signup-img-online.jpeg")}/>
            <View style={{height: 5}}></View>
            <Headline style={{fontWeight: 'bold'}}>Sign Up</Headline>
            <View style={{height: 10}}></View>
            <TextInput
                style={{
                    backgroundColor: "#fff"
                }}
                mode="outlined"
                label="Name"
                placeholder="Type your name"
            />
            <View style={{height: 10}}/>
            <TextInput
                style={{
                    backgroundColor: "#fff"
                }}
                mode="outlined"
                label="Email"
                placeholder="Type your email"
            />
            <View style={{height: 10}}/>
            <TextInput
                style={{
                    backgroundColor: "#fff"
                }}
                mode="outlined"
                label="Phone Number"
                placeholder="Type your phone number"
            />
            <View style={{height: 10}}/>
            <TextInput
                style={{
                    backgroundColor: "#fff"
                }}
                mode="outlined"
                label="Password"
                placeholder="Type your password"
            />
            <View style={{height: 15}}/>
            <Button mode="contained" onPress={() => console.log('Main')}>
                SIGN UP
            </Button>
            <View style={{height: 10}}/>
            <Button mode="text" onPress={() => navigation.navigate('SignIn')}>
                Already Signed Up? Sign In here
            </Button>
            <View style={{height: 30}}/>
            <View style={{height: 20}}/>

        </ScrollView>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        paddingLeft: 15,
        paddingRight: 15,
        paddingTop: 35,
        paddingBottom: 15,
    },
});
export default SignUpScreen;
