import React from "react";
import {useNavigation} from "@react-navigation/native";
import {Button, Card, Headline, TextInput} from "react-native-paper";
import {SafeAreaView, ScrollView, StyleSheet, View} from "react-native";

const SignInScreen = () => {
    const navigation = useNavigation();
    const [searchQuery, setSearchQuery] = React.useState('');
    const onChangeSearch = query => setSearchQuery(query);

    return (
        <ScrollView
            showsVerticalScrollIndicator={false}
            style={styles.container}>
            <SafeAreaView/>
            <Card.Cover source={require("../../assets/signin-image.jpg")}/>
            <View style={{height: 15}}></View>
            <Headline style={{ fontWeight: 'bold'}}>Sign In</Headline>
            <View style={{height: 10}}></View>
            <TextInput
                style={{
                    backgroundColor: "#fff"
                }}
                mode="outlined"
                label="Email"
                placeholder="Type your email"
            />
            <View style={{height: 10}}/>
            <TextInput
                style={{
                    backgroundColor: "#fff"
                }}
                mode="outlined"
                label="Password"
                placeholder="Type your password"
            />
            <View style={{height: 20}}/>
            <Button mode="contained" onPress={() => navigation.navigate('Main')}>
                SIGN IN
            </Button>
            <View style={{height: 20}}/>
            <Button mode="text" onPress={() => navigation.navigate('SignUp')}>
                New Here? Sign Up here
            </Button>
            <View style={{height: 30}}/>
        </ScrollView>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        paddingLeft: 15,
        paddingRight: 15,
        paddingTop: 35,
        paddingBottom: 15,
    },
});
export default SignInScreen;
