import React, {useEffect, useState} from "react";
import {SafeAreaView, ScrollView, StatusBar, StyleSheet, View} from "react-native";
import {Avatar, Button, Caption, Chip, List, RadioButton, TextInput} from 'react-native-paper';
import {useNavigation} from "@react-navigation/native";
import {useStoreActions} from "easy-peasy";
import {Picker} from "@react-native-picker/picker";
import {submitPost} from "../api/firebaseAPI";
import firebase from "../../firebase";

const AddScreen = () => {
    const navigation = useNavigation();
    let currentUserUID = firebase.auth().currentUser.uid;

    const add = useStoreActions((actions) => actions.addExpense);

    let [name, setName] = useState("");
    let [email, setEmail] = useState("");
    let [userName, setUserName] = useState("");
    let [phoneNumber, setPhoneNumber] = useState("");
    let [description, setDescription] = useState("");
    let [website, setWebsite] = useState("");
    const [selectedCategory, setSelectedCategory] = useState();
    const [selectedVariant, setSelectedVariant] = useState();
    let [selectedPrice, setSelectedPrice] = useState();

    const addPost = () => {

        submitPost(
            name,
            phoneNumber,
            website,
            description,
            selectedPrice,
            selectedVariant,
            selectedCategory,
            userName,
            0,
            "logo",
            email
        ).then(r => alert("Posted"));

        navigation.navigate('Main');

        // description = "123";
        // selectedPrice = "123";
        // add({description, selectedPrice});
    };

    useEffect(() => {
        async function getUserInfo() {
            let doc = await firebase
                .firestore()
                .collection('users')
                .doc(currentUserUID)
                .get();
            if (!doc.exists) {
                alert('No user data found!')
            } else {
                let dataObj = doc.data();
                setUserName(dataObj.name);
                setEmail(dataObj.email);
                setPhoneNumber(dataObj.phoneNumber);
            }
        }

        getUserInfo();
    })

    return (
        <ScrollView
            showsVerticalScrollIndicator={false}
            style={styles.container}>
            <SafeAreaView>
                <View style={{height: 4}}/>
                <StatusBar
                    animated={true}
                    backgroundColor="#61dafb"/>
                <View style={{flex: 1, marginTop: 10, alignItems: 'center'}}>
                    <Avatar.Image size={100} source={require('../../assets/favicon.png')}/>
                    <Caption style={{marginTop: 5}}>Product Logo</Caption>
                </View>
                <View style={{height: 10}}/>
                <TextInput
                    style={{
                        backgroundColor: "#fff"
                    }}
                    mode="outlined"
                    label="Product Name"
                    placeholder="Type your product name"
                    autoCapitalize='none'
                    textContentType='name'
                    value={name}
                    autoFocus={true}
                    onChangeText={text => setName(text)}
                />
                <View style={{height: 10}}/>
                <TextInput
                    style={{
                        backgroundColor: "#fff"
                    }}
                    mode="outlined"
                    label="Product Description"
                    placeholder="Type your product description"
                    autoCapitalize='none'
                    textContentType='name'
                    value={description}
                    autoFocus={true}
                    onChangeText={text => setDescription(text)}
                />
                <View style={{height: 10}}/>
                <TextInput
                    style={{
                        backgroundColor: "#fff"
                    }}
                    mode="outlined"
                    label="Product Website"
                    placeholder="Type your product website"
                    autoCapitalize='none'
                    textContentType='name'
                    value={website}
                    autoFocus={true}
                    onChangeText={text => setWebsite(text)}
                />
                <List.Section title="Product Pricing Model">
                    <View style={styles.row}>
                        <Chip
                            selected={true}
                            selectedColor={'#6202ED'}
                            icon="information" onPress={() => {
                            setSelectedPrice('Free')
                        }} style={styles.chip}>
                            Free
                        </Chip>
                        <Chip icon="information" onPress={() => {
                            setSelectedPrice('Mix')
                        }} style={styles.chip}>
                            Mix
                        </Chip>
                        <Chip icon="information" onPress={() => {
                            setSelectedPrice('Paid')
                        }} style={styles.chip}>
                            Paid
                        </Chip>
                    </View>
                </List.Section>
                <List.Section title="Product Operating System">
                    <RadioButton.Group
                        style={{
                            flexDirection: 'column',
                            flexWrap: 'wrap',
                            flex: 1,
                        }}
