import React from "react";
import {FlatList, SafeAreaView, StyleSheet, View} from "react-native";
import {HomeScreenList, MainContainer} from "../components";
import {Chip} from "react-native-paper";

const HomeScreen = () => {

    const chipFilter = [
        {
            icon: '58694a0f-3da1-471f-bd96-145571e29d72',
            title: 'Featured Products',
            id: 1
        },
        {
            icon: 'bd7acbea-c1b1-46c2-aed5-3ad53abb28ba',
            title: 'Most Recent',
            id: 2
        },
        {
            icon: '3ac68afc-c605-48d3-a4f8-fbd91aa97f63',
            title: 'Trending',
            id: 3
        },
        {
            icon: '58694a0f-3da1-471f-bd96-145571e29d72',
            title: 'Top Voted',
            id: 4
        },
        {
            icon: '58694a0f-3da1-471f-bd96-145571e29d72',
            title: 'Top 100',
            id: 5
        },
        {
            icon: '58694a0f-3da1-471f-bd96-145571e29d72',
            title: 'Free Products',
            id: 6
        },
        {
            icon: '58694a0f-3da1-471f-bd96-145571e29d72',
            title: 'Paid Products',
            id: 7
        },
        {
            icon: '58694a0f-3da1-471f-bd96-145571e29d72',
            title: 'Mix Products',
            id: 8
        },


    ];


    const Item = ({title}) => (
        <View style={styles.item}>
            <Chip style={{
                backgroundColor:'lightgray'
            }} icon="information">{title}</Chip>
        </View>
    );

    const renderItem = ({item}) => (
        <Item title={item.title}/>
    );

    return (
        <View style={styles.container}>
            <SafeAreaView/>
            <MainContainer>
                <FlatList
                    style={{
                        paddingHorizontal: 10,
                        marginTop: 20,
                        height: 50,
                        flexGrow: 0
                    }}
                    data={chipFilter}
                    horizontal
                    pagingEnabled={true}
                    showsHorizontalScrollIndicator={false}
                    legacyImplementation={false}
                    renderItem={renderItem}
                    keyExtractor={item => item.id}
                />
                <HomeScreenList/>
            </MainContainer>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        height: '100%',
        alignItems: "center",
        justifyContent: "center",
    },
});

export default HomeScreen;
