import React from "react";
import {FlatList, View} from "react-native";
import {Avatar, Caption, Card, Snackbar, Subheading, TouchableRipple} from 'react-native-paper';
import {Ionicons} from "@expo/vector-icons";
import {useNavigation} from "@react-navigation/native";

const HomeScreenList = ({expenses}) => {
        const navigation = useNavigation();
        const [visible, setVisible] = React.useState(false);
        const onToggleSnackBar = () => setVisible(!visible);
        const onDismissSnackBar = () => setVisible(false);



        return (
            <FlatList
                style={{flex: 1, width: '100%', paddingHorizontal: 10, marginTop: 20}}
                data={expenses}
                showsVerticalScrollIndicator={false}
                renderItem={({item}) => (
                    <Card
                        onPress={() => navigation.navigate("HomeDetail")}
                        elevation={1}
                        mode={"elevated"}
                        style={{marginVertical: 5, borderRadius: 4}}>
                        <Card.Title
                            title="Card Title"
                            subtitle="Card Subtitle"
                            left={(props) =>
                                <Avatar.Image {...props}
                                              size={45}
                                              source={{uri: 'https://ph-files.imgix.net/206c70ae-d2b6-4496-b1da-76746f2ddefb.png?auto=format&auto=compress&codec=mozjpeg&cs=strip&w=80&h=80&fit=crop&bg=0fff&dpr=2'}}/>
                            }
                            rightStyle={{marginRight: 10}}
                            right={(props) =>
                                <TouchableRipple>
                                    <View style={{
                                        flex: 1,
                                        borderWidth: 1,
                                        borderColor: 'lightgray',
                                        borderRadius: 10,
                                        padding: 5,
                                        marginTop: 5,
                                        justifyContent: 'center',
                                        flexDirection: 'column',
                                        alignItems: 'center'
                                    }}>
                                        <Ionicons name="caret-up" size={25}/>
                                        <Subheading>1</Subheading>
                                    </View>
                                </TouchableRipple>
                            }
                        />
                        <View
                            style={{
                                flexDirection: 'row',
                                justifyContent: 'center',
                                alignContent: 'center',
                                flex: 3,
                                flexWrap: 'wrap',
                                paddingHorizontal: 10,
                                marginTop: 5,
                                alignItems: 'center'
                            }}>
                            <Caption style={{
                                justifyContent: 'center', alignItems: 'center',
                                flex: 1, textAlignVertical: "center"
                            }}>by coderraja</Caption>
                            <Caption style={{
                                justifyContent: 'center', alignItems: 'center',
                                flex: 1, textAlignVertical: "center"
                            }}>Free Options</Caption>
                            <Caption style={{
                                justifyContent: 'center', alignItems: 'center',
                                flex: 1, textAlignVertical: "center"
                            }}>Social Media Tool</Caption>
                        </View>
                    </Card>

                )}
                keyExtractor={(item) => item.id}
            />
        );
    }
;

export default HomeScreenList;
