import React from "react";
import {SafeAreaView, ScrollView, StatusBar, StyleSheet, View} from "react-native";
import {Avatar, Button, TextInput} from 'react-native-paper';

const ProfileScreen = () => {
    const [name, setName] = React.useState('');
    const [bio, setBio] = React.useState('');
    const [email, setEmail] = React.useState('');
    const [password, setPassword] = React.useState('');
    const [website, setWebsite] = React.useState('');

    return (
        <ScrollView
            showsVerticalScrollIndicator={false}
            style={styles.container}>
            <SafeAreaView/>
            <View style={{height: 4}}/>
            <StatusBar
                animated={true}
                backgroundColor="#61dafb"/>
            <View style={{flex: 1, marginTop: 10, alignItems: 'center'}}>
                <Avatar.Image size={100} source={require('../../assets/favicon.png')}/>
            </View>
            <View style={{height: 10}}/>
            <TextInput
                style={{
                    backgroundColor: "#fff"
                }}
                mode="outlined"
                label="Name"
                placeholder="Type your name"
            />
            <View style={{height: 10}}/>
            <TextInput
                style={{
                    backgroundColor: "#fff"
                }}
                mode="outlined"
                label="Email"
                placeholder="Type your email"
            />
            <View style={{height: 10}}/>
            <TextInput
                style={{
                    backgroundColor: "#fff"
                }}
                mode="outlined"
                label="Password"
                placeholder="Type your password"
            />
            <View style={{height: 10}}/>
            <TextInput
                style={{
                    backgroundColor: "#fff"
                }}
                mode="outlined"
                label="Bio"
                placeholder="Type your bio"
            />
            <View style={{height: 10}}/>
            <TextInput
                style={{
                    backgroundColor: "#fff"
                }}
                mode="outlined"
                label="Website"
                placeholder="Type your website link"
            />
            <View style={{height: 20}}/>
            <Button icon="account" mode="contained" onPress={() => console.log('Pressed')}>
                Update
            </Button>
            <View style={{height: 20}}/>
            <Button icon="hammer-screwdriver" mode="contained" onPress={() => console.log('Pressed')}>
                Settings
            </Button>
            <View style={{height: 30}}/>
        </ScrollView>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        paddingLeft: 15,
        paddingRight: 15,
        paddingTop: 15,
        paddingBottom: 15,
        backgroundColor: "#fff",
    },
});

export default ProfileScreen;
