import React from "react";
import {createStackNavigator} from "@react-navigation/stack";
import {createBottomTabNavigator} from "@react-navigation/bottom-tabs";
import {Ionicons} from "@expo/vector-icons";
import {AddScreen, ModifyScreen, MonthScreen, TodayScreen, WeekScreen,} from "./screens";

const RootStack = createStackNavigator();
const Tab = createBottomTabNavigator();

const TabsNavigator = () => {
    return (
        <Tab.Navigator
            initialRouteName="Week"
            tabBarOptions={{
                activeTintColor: '#fff',
                inactiveTintColor: 'lightgray',
                activeBackgroundColor: 'rgba(246,73,0,0.62)',
                inactiveBackgroundColor: 'rgba(246,73,0,0.62)',
                style: {
                    backgroundColor: '#f64900',
                    paddingBottom: 10
                }
            }}>
            <Tab.Screen
                name="Week"
                component={WeekScreen}
                options={{
                    tabBarIcon: () => <Ionicons name="home" size={20}/>,
                }}
            />
            <Tab.Screen
                name="Today"
                component={TodayScreen}
                options={{
                    tabBarIcon: () => <Ionicons name="search" size={20}/>,
                }}
            />
            <Tab.Screen
                name="Month"
                component={MonthScreen}
                options={{
                    tabBarIcon: () => <Ionicons name="person" size={20}/>,
                }}
            />
        </Tab.Navigator>
    );
};

const RootStackScreen = () => {
    return (
        <RootStack.Navigator mode="modal">
            <RootStack.Screen
                name="Main"
                component={TabsNavigator}
                options={{headerShown: false}}
            />
            <RootStack.Screen name="Add" component={AddScreen}/>
            <RootStack.Screen name="Modify" component={ModifyScreen}/>
        </RootStack.Navigator>
    );
};

export default RootStackScreen;
