import React from "react";
import {FlatList, Text, View} from "react-native";
import {Avatar, Caption, Card} from 'react-native-paper';
import {Ionicons} from "@expo/vector-icons";

const ExpenseList = ({expenses}) => {
    return (
        <FlatList
            style={{flex: 1, width: '100%', paddingHorizontal: 10}}
            data={expenses}
            showsVerticalScrollIndicator={false}
            renderItem={({item}) => (
                <View>
                    <Card
                        mode={"elevated"}>
                        <Card.Title
                            title="Card Title"
                            subtitle="Card Subtitle"
                            left={(props) =>
                                <Avatar.Image {...props}
                                              size={45}
                                              source={{uri: 'https://ph-files.imgix.net/206c70ae-d2b6-4496-b1da-76746f2ddefb.png?auto=format&auto=compress&codec=mozjpeg&cs=strip&w=80&h=80&fit=crop&bg=0fff&dpr=2'}}/>
                            }
                            rightStyle={{marginRight: 10}}
                            right={(props) =>
                                <View>
                                <Ionicons name="caret-up" size={25}/>
                                    <Text>1</Text>
                                </View>}
                        />
                        <View
                            style={{flexDirection: 'row', flex: 1}}>
                            <Caption style={{flex: 0.3}}>Free</Caption>
                            <Caption style={{flex: 0.5}}>Caption</Caption>
                        </View>
                    </Card>
                </View>

            )}
            keyExtractor={(item) => item.id}
        />
    );
};

export default ExpenseList;
