import React, {useState} from "react";
import {Linking, SafeAreaView, ScrollView, StatusBar, StyleSheet, View} from "react-native";
import {Avatar, Button, Card, Chip, Paragraph, Title} from 'react-native-paper';
import {useNavigation} from "@react-navigation/native";
import {useStoreActions} from "easy-peasy";

const HomeDetailScreen = () => {
    const navigation = useNavigation();
    const add = useStoreActions((actions) => actions.addExpense);

    const [description, setDescription] = useState("");
    const [amount, setAmount] = useState("");
    const [selectedCategory, setSelectedCategory] = useState();
    const [selectedVariant, setSelectedVariant] = useState();
    const [selectedPrice, setSelectedPrice] = useState();

    const addExpense = () => {
        if (description !== "" && amount !== "") {
            add({description, amount});
            navigation.navigate("Home");
        }
    };

    return (
        <ScrollView
            showsVerticalScrollIndicator={false}
            style={styles.container}>
            <SafeAreaView>
                <View style={{height: 4}}/>
                <StatusBar
                    animated={true}
                    backgroundColor="#61dafb"/>
                <View style={{flex: 1, marginTop: 10, alignItems: 'center'}}>
                    <Avatar.Image size={100} source={require('../../assets/favicon.png')}/>
                </View>

                <View style={{height: 10}}/>
                <Card>
                    <Card.Content>
                        <Title>Product Name</Title>
                        <View style={{height: 10}}/>
                        <Paragraph>Product Description</Paragraph>
                    </Card.Content>
                    <View style={{height: 10}}/>
                    <View style={styles.row}>
                        <Chip icon="information" onPress={() => {
                        }} style={styles.chip}>
                            Free Options
                        </Chip>
                        <Chip icon="app" onPress={() => {
                        }} style={styles.chip}>
                            Android
                        </Chip>
                    </View>
                    <View style={{height: 10}}/>
                    <View style={styles.row}>
                        <Chip icon="information" onPress={() => {
                        }} style={styles.chip}>
                            Social Media
                        </Chip>
                    </View>
                </Card>
                <View style={{height: 20}}/>
                <Button icon="link" mode="contained"
                        onPress={() => Linking.openURL('mailto:rajakaleemlab@gmail.com?subject=ProductExplore | Feedback&body=Hello!')}>
                    VISIT PRODUCT WEBSITE
                </Button>
                <View style={{height: 30}}/>
            </SafeAreaView>
        </ScrollView>
    );
};
const styles = StyleSheet.create({
    container: {
        flex: 1,
        paddingLeft: 15,
        paddingRight: 15,
        marginTop: 20,
        paddingBottom: 15,
        backgroundColor: "#fff",
    },
    row: {
        flexDirection: 'row',
        flexWrap: 'wrap',
        flex: 1,
    },
    chip: {
        margin: 4,
        flex: 0.50,
        backgroundColor: 'lightgray'
    },
});

export default HomeDetailScreen;
