import React from "react";
import {createStackNavigator} from "@react-navigation/stack";
import {createBottomTabNavigator} from "@react-navigation/bottom-tabs";
import {Ionicons} from "@expo/vector-icons";
import {AddScreen, HomeScreen, ModifyScreen, MonthScreen, TodayScreen,} from "./screens";
import {Platform} from "react-native";

const RootStack = createStackNavigator();
const Tab = createBottomTabNavigator();

const TabsNavigator = () => {
    return (
        <Tab.Navigator
            initialRouteName="Week"
            shifting={true}
            labeled={false}
            sceneAnimationEnabled={false}
            activeColor="#00aea2"
            inactiveColor="#95a5a6"
            barStyle={{backgroundColor: '#ffff'}}
            tabBarOptions={{
                showLabel: false,
                activeTintColor: '#fff',
                inactiveTintColor: 'lightgray',
                activeBackgroundColor: '#c4461c',
                inactiveBackgroundColor: '#b55031',
                style: {
                    paddingBottom: 10,
                    paddingTop: 4,
                    paddingVertical: Platform.OS === 'ios' ? 20 : 0,
                    height: 40,
                }

            }}>
            <Tab.Screen
                name="Week"
                component={HomeScreen}
                options={{
                    tabBarIcon: () => <Ionicons name="home" size={24}/>,
                }}
            />
            <Tab.Screen
                name="Today"
                component={TodayScreen}
                options={{
                    tabBarIcon: () => <Ionicons name="search" size={24}/>,
                }}
            />
            <Tab.Screen
                name="Month"
                component={MonthScreen}
                options={{
                    tabBarIcon: () => <Ionicons name="person" size={24}/>,
                }}
            />
        </Tab.Navigator>
    );
};

const RootStackScreen = () => {
    return (
        <RootStack.Navigator mode="modal">
            <RootStack.Screen
                name="Main"
                component={TabsNavigator}
                options={{headerShown: false}}
            />
            <RootStack.Screen name="Add" component={AddScreen}/>
            <RootStack.Screen name="Modify" component={ModifyScreen}/>
        </RootStack.Navigator>
    );
};

export default RootStackScreen;
